import React, { useRef, useEffect, useState } from 'react';
import styled from 'styled-components';

const WaveformContainer = styled.div`
  width: 100%;
  height: 100px;
  position: relative;
  background-color: #f1f3f4;
  border-radius: 4px;
  overflow: hidden;
  cursor: pointer;
`;

const WaveformCanvas = styled.canvas`
  width: 100%;
  height: 100%;
`;

const ProgressOverlay = styled.div<{ progress: number }>`
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: ${props => props.progress}%;
  background-color: rgba(255, 85, 0, 0.2);
  pointer-events: none;
`;

interface AudioWaveformProps {
  audioUrl: string;
  currentTime: number;
  duration: number;
  isPlaying: boolean;
  onSeek: (time: number) => void;
  onPlayPause: () => void;
}

const AudioWaveform: React.FC<AudioWaveformProps> = ({
  audioUrl,
  currentTime,
  duration,
  isPlaying,
  onSeek,
  onPlayPause
}) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const [waveformData, setWaveformData] = useState<number[]>([]);

  useEffect(() => {
    if (audioUrl) {
      fetchAudioAndComputeWaveform(audioUrl);
    }
  }, [audioUrl]);

  useEffect(() => {
    drawWaveform();
  }, [waveformData, currentTime]);

  const fetchAudioAndComputeWaveform = async (url: string) => {
    try {
      const response = await fetch(url);
      const arrayBuffer = await response.arrayBuffer();
      const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
      const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);
      const waveform = computeWaveform(audioBuffer);
      setWaveformData(waveform);
    } catch (error) {
      console.error('Error fetching audio:', error);
    }
  };

  const computeWaveform = (audioBuffer: AudioBuffer) => {
    const channelData = audioBuffer.getChannelData(0);
    const samples = 199;
    const blockSize = Math.floor(channelData.length / samples);
    const waveform = [];

    for (let i = 0; i < samples; i++) {
      const start = i * blockSize;
      const end = start + blockSize;
      let max = 0;
      for (let j = start; j < end; j++) {
        const amplitude = Math.abs(channelData[j]);
        if (amplitude > max) {
          max = amplitude;
        }
      }
      waveform.push(max);
    }

    return waveform;
  };

  const drawWaveform = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const width = canvas.width;
    const height = canvas.height;
    const barWidth = width / waveformData.length;

    ctx.clearRect(0, 0, width, height);

    waveformData.forEach((value, index) => {
      const x = index * barWidth;
      const barHeight = value * height;

      ctx.fillStyle = currentTime / duration > index / waveformData.length ? '#ff5500' : '#999';
      ctx.fillRect(x, (height - barHeight) / 2, barWidth - 1, barHeight);
    });
  };

  const handleClick = (e: React.MouseEvent<HTMLDivElement>) => {
    if (!containerRef.current) return;
    const rect = containerRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const seekTime = (x / rect.width) * duration;
    onSeek(seekTime);
  };

  const handleDoubleClick = () => {
    onPlayPause();
  };

  return (
    <WaveformContainer 
      ref={containerRef} 
      onClick={handleClick} 
      onDoubleClick={handleDoubleClick}
    >
      <WaveformCanvas ref={canvasRef} width={800} height={100} />
      <ProgressOverlay progress={(currentTime / duration) * 100} />
    </WaveformContainer>
  );
};

export default AudioWaveform;
