import React, { useState } from 'react';
import { Box, Typography, Slider } from '@material-ui/core';

const Timestamps: React.FC = () => {
  const [blockSize, setBlockSize] = useState<number>(5);

  const timeBlocks = 60 / blockSize;

  const handleSliderChange = (event: any, newValue: number | number[]) => {
    if (typeof newValue === 'number') {
      setBlockSize(newValue);
    }
  };

  const formatTime = (start: number, end: number): string => {
    const format = (time: number) => String(time).padStart(2, '0');
    const formattedEnd = end <= 59 ? format(end) : '00';
    return `${format(start)}:00:${formattedEnd}`;
};

  const handleBlockClick = (start: number) => {
    alert(`Начало отрезка: ${formatTime(start, start + blockSize)}`); // Вывод алерта с началом отрезка при клике
  };

  return (
    <>
    <Slider
      min={5}
      max={30}
      value={blockSize}
      onChange={handleSliderChange}
      aria-labelledby="step-slider"
      valueLabelDisplay="auto"
      step={5}
    />
    <Box display="flex" flexDirection="column" style={{gap: '1rem'}}>
      {Array.from(Array(timeBlocks)).map((_, index) => (
        <Box key={index} onClick={() => handleBlockClick(index * blockSize)} style={{ cursor: 'pointer', padding: '10px', background: 'lightblue' }}>
          <Typography>{formatTime(index * blockSize, (index + 1) * blockSize)}</Typography>
        </Box>
      ))}
    </Box>
  </>
  );
};

export default Timestamps;
