import React, { useRef, useEffect, useState } from 'react';

interface WaveformProps {
  audioUrl: string;
}

const WaveFormZoomTest: React.FC<WaveformProps> = ({ audioUrl }) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [zoom, setZoom] = useState(1);
  const [scrollPosition, setScrollPosition] = useState(0);
  const [audioData, setAudioData] = useState<number[]>([]);

  useEffect(() => {
    // ... (код загрузки аудио остается без изменений)
  }, [audioUrl]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const dpr = window.devicePixelRatio || 1;
    const parentWidth = canvas.parentElement?.clientWidth || 0;
    const canvasWidth = parentWidth * 0.8;
    canvas.width = canvasWidth * dpr;
    canvas.height = 300 * dpr;
    canvas.style.width = `${canvasWidth}px`;
    canvas.style.height = '300px';
    ctx.scale(dpr, dpr);

    const drawWaveform = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      ctx.beginPath();

      const width = canvasWidth;
      const height = 300;
      const step = (width * zoom) / audioData.length;

      for (let i = 0; i < audioData.length; i++) {
        const x = i * step - scrollPosition;
        if (x < -step) continue;
        if (x > width) break;
        const y = (1 - audioData[i]) * height / 2;
        if (i === 0) {
          ctx.moveTo(x, y);
        } else {
          ctx.lineTo(x, y);
        }
      }

      ctx.strokeStyle = 'blue';
      ctx.stroke();
    };

    drawWaveform();
  }, [audioData, zoom, scrollPosition]);

  const handleZoomIn = () => {
    setZoom(prev => {
      const newZoom = Math.min(prev * 2, 64);
      setScrollPosition(prev => prev * (newZoom / prev));
      return newZoom;
    });
  };

  const handleZoomOut = () => {
    setZoom(prev => {
      const newZoom = Math.max(prev / 2, 1);
      setScrollPosition(prev => prev * (newZoom / prev));
      return newZoom;
    });
  };

  const handleScroll = (e: React.ChangeEvent<HTMLInputElement>) => {
    setScrollPosition(Number(e.target.value));
  };

  return (
    <div style={{ width: '100%' }}>
      <div style={{ width: '80%', margin: '0 auto' }}>
        <canvas ref={canvasRef} style={{ border: '1px solid black' }} />
      </div>
      <div>
        <button onClick={handleZoomOut} disabled={zoom === 1}>Zoom Out</button>
        <button onClick={handleZoomIn} disabled={zoom === 64}>Zoom In</button>
      </div>
      {zoom > 1 && (
        <input
          type="range"
          min={0}
          max={canvasRef.current ? (canvasRef.current.width / window.devicePixelRatio) * (zoom - 1) : 0}
          value={scrollPosition}
          onChange={handleScroll}
          style={{ width: '80%', margin: '10px auto', display: 'block' }}
        />
      )}
    </div>
  );
};

export default WaveFormZoomTest;
