import React, { useState, useRef, useEffect, useCallback } from 'react';

interface AudioPlayerProps {
  audioUrl: string;
}

const Begunok3: React.FC<AudioPlayerProps> = ({ audioUrl }) => {
  const [isPlaying, setIsPlaying] = useState(false);
  const [isExpanded, setIsExpanded] = useState(false);
  const [currentTime, setCurrentTime] = useState(0);
  const [duration, setDuration] = useState(0);
  const [zoomLevel, setZoomLevel] = useState(1);
  const [scrollPosition, setScrollPosition] = useState(0);

  const audioRef = useRef<HTMLAudioElement>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const waveformData = useRef<number[]>([]);
  const animationRef = useRef<number>();

  const [audioData, setAudioData] = useState<number[]>([]);
  const [audioDuration, setAudioDuration] = useState(0);

  let channelNumber:Number = 1

  const drawWaveform = useCallback(() => {
    if (!canvasRef.current || !audioData ) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d')!;
    const dpr = window.devicePixelRatio || 1;
    const padding = 5;

    canvas.width = canvas.offsetWidth * dpr;
    canvas.height = (isExpanded ? 300 : 80) * dpr;

    ctx.scale(dpr, dpr);
    ctx.clearRect(0, 0, canvas.width, canvas.height);

    const width = canvas.width / dpr;
    const height = canvas.height / dpr;
    const barWidth = width / audioData.length;
    const visibleBars = Math.floor(width / barWidth);
    const startIndex = Math.floor((currentTime / audioDuration) * (audioData.length - visibleBars));

    if (isExpanded && channelNumber === 2) {
      // Отрисовка двух каналов
      for (let channel = 0; channel < 2; channel++) {
        const channelHeight = height / 2;
        const yOffset = channel * channelHeight;

        ctx.fillStyle = channel === 0 ? '#b7e1dd' : '#e1b7dd';
        ctx.strokeStyle = channel === 0 ? '#b7e1dd' : '#e1b7dd';

        for (let i = 0; i < visibleBars; i++) {
          const dataIndex = startIndex + i;
          if (dataIndex >= audioData.length) break;

          const x = i * barWidth;
          const barHeight = audioData[dataIndex] * (channelHeight - padding * 2);

          ctx.beginPath();
          ctx.lineTo(x + barWidth / 2, yOffset + channelHeight / 2 + padding - barHeight / 2);
          ctx.lineTo(x + barWidth / 2, yOffset + channelHeight / 2 + padding + barHeight / 2);
          ctx.stroke();
        }

        // Draw progress for each channel
        const progressWidth = (currentTime / audioDuration) * width;
        ctx.fillStyle = channel === 0 ? '#357F78' : '#7F3578';
        ctx.strokeStyle = channel === 0 ? '#357F78' : '#7F3578';

        ctx.save();
        ctx.beginPath();
        ctx.rect(0, yOffset, progressWidth, channelHeight);
        ctx.clip();

        for (let i = 0; i < visibleBars; i++) {
          const dataIndex = startIndex + i;
          if (dataIndex >= audioData.length) break;

          const x = i * barWidth;
          const barHeight = audioData[dataIndex] * (channelHeight - padding * 2);

          ctx.beginPath();
          ctx.lineTo(x + barWidth / 2, yOffset + channelHeight / 2 + padding - barHeight / 2);
          ctx.lineTo(x + barWidth / 2, yOffset + channelHeight / 2 + padding + barHeight / 2);
          ctx.stroke();
        }

        ctx.restore();

        // Draw playhead (vertical line)
        ctx.beginPath();
        ctx.strokeStyle = '#357F78';
        ctx.lineWidth = 1;
        ctx.moveTo(progressWidth, 0);
        ctx.lineTo(progressWidth, height);
        ctx.stroke();
      }
    } else {
      // Отрисовка общей дорожки (как было раньше)
      ctx.fillStyle = '#b7e1dd';
      ctx.strokeStyle = '#b7e1dd';

      for (let i = 0; i < visibleBars; i++) {
        const dataIndex = startIndex + i;
        if (dataIndex >= audioData.length) break;

        const x = i * barWidth;
        const barHeight = audioData[dataIndex] * (height - padding * 2);

        ctx.beginPath();
        ctx.lineTo(x + barWidth / 2, height / 2 + padding - barHeight / 2);
        ctx.lineTo(x + barWidth / 2, height / 2 + padding + barHeight / 2);
        ctx.stroke();
      }

      // Draw progress
      const progressWidth = (currentTime / audioDuration) * width;
      ctx.fillStyle = '#357F78';
      ctx.strokeStyle = '#357F78';

      ctx.save();
      ctx.beginPath();
      ctx.rect(0, 0, progressWidth, height);
      ctx.clip();

      for (let i = 0; i < visibleBars; i++) {
        const dataIndex = startIndex + i;
        if (dataIndex >= audioData.length) break;

        const x = i * barWidth;
        const barHeight = audioData[dataIndex] * (height - padding * 2);

        ctx.beginPath();
        ctx.lineTo(x + barWidth / 2, height / 2 + padding - barHeight / 2);
        ctx.lineTo(x + barWidth / 2, height / 2 + padding + barHeight / 2);
        ctx.stroke();
      }

      ctx.restore();

      // Draw playhead (vertical line)
      ctx.beginPath();
      ctx.strokeStyle = '#357F78';
      ctx.lineWidth = 1;
      ctx.moveTo(progressWidth, 0);
      ctx.lineTo(progressWidth, height);
      ctx.stroke();
    }
  }, [isExpanded, currentTime, audioDuration, audioData, ]);

  useEffect(() => {
    const fetchAudio = async () => {
      const response = await fetch(audioUrl);
      const arrayBuffer = await response.arrayBuffer();
      const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
      const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);
      const channelData = audioBuffer.getChannelData(0);

      // Use the full audio data instead of downsampling
      const filteredData = Array.from(channelData);

      setAudioData(filteredData);
      setAudioDuration(audioBuffer.duration);
    };

    fetchAudio();
  }, [audioUrl]);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const dpr = window.devicePixelRatio || 1;
    const parentWidth = canvas.parentElement?.clientWidth || 0;
    const canvasWidth = parentWidth;
    canvas.width = canvasWidth * dpr;
    canvas.height = 300 * dpr;
    canvas.style.width = `${canvasWidth}px`;
    canvas.style.height = '300px';

    drawWaveform();
  }, [drawWaveform]);

  const handleCanvasClick = (e: React.MouseEvent<HTMLCanvasElement>) => {
    const canvas = canvasRef.current;
    if (!canvas || !audioRef.current) return;

    const rect = canvas.getBoundingClientRect();
    const x = e.clientX - rect.left + scrollPosition;
    const clickProgress = x / (rect.width * zoomLevel);

    audioRef.current.currentTime = clickProgress * duration;
    setCurrentTime(clickProgress * duration);
  };

  const handleScroll = (e: React.WheelEvent<HTMLCanvasElement>) => {
    if (isExpanded) {
      const newScrollPosition = scrollPosition + e.deltaY;
      const maxScroll = canvasRef.current!.width * (zoomLevel - 1);
      setScrollPosition(Math.max(0, Math.min(newScrollPosition, maxScroll)));
    }
  };

  const smoothZoom = (targetZoom: number, duration: number = 300) => {
    const startZoom = zoomLevel;
    const startTime = performance.now();

    const animate = (currentTime: number) => {
      const elapsed = currentTime - startTime;
      const progress = Math.min(elapsed / duration, 1);
      const easeProgress = progress * (2 - progress); // easeOutQuad

      const newZoom = startZoom + (targetZoom - startZoom) * easeProgress;
      setZoomLevel(newZoom);

      if (progress < 1) {
        requestAnimationFrame(animate);
      }
    };

    requestAnimationFrame(animate);
  };

  const zoomIn = () => {
    const newZoom = Math.min(zoomLevel * 2, 64);
    smoothZoom(newZoom);
  };

  const zoomOut = () => {
    const newZoom = Math.max(zoomLevel / 2, 1);
    smoothZoom(newZoom);
  };

  const togglePlay = () => {
    if (audioRef.current?.paused) {
      audioRef.current.play();
    } else {
      audioRef.current?.pause();
    }
    setIsPlaying(!isPlaying);
  };

  const toggleExpand = () => {
    setIsExpanded(!isExpanded);
    setZoomLevel(1);
    setScrollPosition(0);
  };

  const formatTime = (time: number) => {
    const minutes = Math.floor(time / 60);
    const seconds = Math.floor(time % 60);
    return `${minutes}:${seconds.toString().padStart(2, '0')}`;
  };

  return (
    <div className="audio-player">
      <button onClick={togglePlay}>{isPlaying ? 'Pause' : 'Play'}</button>
      <button onClick={toggleExpand}>
        {isExpanded ? 'Collapse' : 'Expand'}
      </button>
      <div style={{ display: 'flex', justifyContent: 'center', width: '98%' }}>
        <audio ref={audioRef} src={audioUrl} />
        <canvas
          ref={canvasRef}
          onClick={handleCanvasClick}
          onWheel={handleScroll}
          style={{
            width: '100%',
            height: isExpanded ? '300px' : '80px',
            cursor: 'pointer'
          }}
        />
      </div>
      <div>
        {formatTime(currentTime)} / {formatTime(duration)}
      </div>
      {isExpanded && (
        <div>
          <button onClick={zoomIn}>Zoom In</button>
          <button onClick={zoomOut}>Zoom Out</button>
          <span>Zoom: {zoomLevel.toFixed(2)}x</span>
        </div>
      )}
    </div>
  );
};

export default Begunok3;
