import React, { FC, useMemo, useRef, useState, useEffect, useCallback } from "react";
import IconButton from "@material-ui/core/IconButton";
import PlayCircleFilledIcon from "@material-ui/icons/PlayCircleFilled";
import PauseCircleFilledIcon from "@material-ui/icons/PauseCircleFilled";
import ZoomInIcon from "@material-ui/icons/ZoomIn";
import ZoomOutIcon from "@material-ui/icons/ZoomOut";
import GetAppIcon from "@material-ui/icons/GetApp";
import Checkbox from "@material-ui/core/Checkbox";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import { WaveSurfer, WaveForm, Region } from "wavesurfer-react";
import { currTimeSubscriber, changeSegmentPositionSubscriber } from "../../subscribers/PlayerSubscriber";
import { SegmentModel as Segment } from "../AudioContext";
// import { PluginType } from "wavesurfer-react/dist/types";
// import { GenericPlugin } from "wavesurfer.js/dist/base-plugin";
// import Timeline from 'wavesurfer.js/dist/plugins/timeline.esm.js'

interface Props {
  file: File | null;
  segments: Segment[];
  activeSegmentProp: Segment | null;
  url: string;
}
const RegionsPlugin = require("wavesurfer.js/dist/plugin/wavesurfer.regions.min");
const TimelinePlugin = require("wavesurfer.js/dist/plugin/wavesurfer.timeline.min");
// import RegionsPlugin, { Region as RegionWS, RegionParams } from "wavesurfer.js/dist/plugins/regions";

const WavePlayer: FC<Props> = ({ file, segments, activeSegmentProp, url }) => {
  const [isPlay, setIsPlay] = useState<boolean>(false);
  const [autoCenter, setAutoCenter] = useState<boolean>(true);
  const [zoom, setZoom] = useState<number>(10);
  const [regions, setRegions] = useState<any[]>([]);
  const [activeSegment, setActiveSegment] = useState<Segment | null>(activeSegmentProp);

  const plugins= useMemo(() => [
    {
      plugin: RegionsPlugin,
      options: { dragSelection: false, showTooltip: false },
    },
    {
      plugin: TimelinePlugin,
      options: { container: "#timeline" },
    },
  ], []);

  const wavesurferRef = useRef<any>(null);

  const handleWSMount = useCallback((waveSurfer) => {
    wavesurferRef.current = waveSurfer;
    wavesurferRef.current.load(url);

    wavesurferRef.current.on("ready", () => {
      console.log("WaveSurfer is ready");
    });

    wavesurferRef.current.on("region-update-end", (region: any) => {
      changeSegmentPositionSubscriber.next({
        segment: region.data,
        start: region.start,
        end: region.end,
      });
    });

    let lastTime = 0;
    wavesurferRef.current.on("audioprocess", (currentTime: number) => {
      const diff = Math.abs(currentTime - lastTime);
      if (diff > 0.1) {
        lastTime = currentTime;
        currTimeSubscriber.next(currentTime);
      }
    });
  }, [url]);

  const setZoomClick = (zoom: number) => {
    if (zoom < 5) zoom = 5;
    if (zoom > 200) zoom = 200;
    setZoom(zoom);
  };

  const downloadAudio = () => {
    fetch(url).then(res => {
      if (!file) return;
      res.blob().then(blob => {
        let name = file.name;
        if (!name.endsWith(".wav")) name += ".wav";

        const url = window.URL.createObjectURL(blob);
        const a = document.createElement("a");
        a.href = url;
        a.download = name;
        a.click();
        a.remove();
      });
    });
  };

  const handleKeyDown = useCallback(
    (event: KeyboardEvent) => {
      if (!wavesurferRef.current) return;

      const { key, ctrlKey } = event;
      const isInputActive = document.activeElement?.tagName === "TEXTAREA" || document.activeElement?.tagName === "INPUT";
      const isCtrlPressed = isInputActive ? ctrlKey : true;

      if (key === " " && isCtrlPressed) {
        setIsPlay(prev => !prev);
      }

      if ((key === "w" || key === "s") && isCtrlPressed) {
        event.preventDefault();
        event.stopPropagation();
      }

      if ((key === "a" || key === "d" || key === "ф" || key === "в") && isCtrlPressed) {
        let diff = 5;

if (key === "a" || key === "ф") diff *= -1;

        const total = wavesurferRef.current.getDuration();
        let need = wavesurferRef.current.getCurrentTime() + diff;
        if (need < 0) need = 0;
        if (need > total) need = total;
        if (activeSegment && need < activeSegment.Start - 1) need = activeSegment.Start - 1;

        wavesurferRef.current.setCurrentTime(need);
        setIsPlay(true);

        event.preventDefault();
        event.stopPropagation();
      }
    },
    [activeSegment]
  );

  useEffect(() => {
    window.addEventListener("keydown", handleKeyDown);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
    };
  }, [handleKeyDown]);

  useEffect(() => {
    const mappedRegions = segments.map(s => ({
      id: s.id,
      start: s.Start,
      end: s.End,
      color: "#6ab4ff29",
      drag: false,
      resize: true,
      handleStyle: {
        left: { backgroundColor: "transparent", width: "5px" },
        right: { backgroundColor: "transparent", width: "5px" },
      },
      data: s,
    }));
    setRegions(mappedRegions);
  }, [segments]);

  useEffect(() => {
    if (!wavesurferRef.current) return;

    setZoom(10);

    if (url === "") return;

    wavesurferRef.current.load(url);
  }, [file, url]);

  useEffect(() => {
    if (!wavesurferRef.current) return;

    if (wavesurferRef.current.params.minPxPerSec !== zoom) {
      wavesurferRef.current.zoom(zoom);
    }
  }, [zoom]);

  useEffect(() => {
    if (!wavesurferRef.current) return;

    if (isPlay) {
      wavesurferRef.current.play();
    } else {
      wavesurferRef.current.pause();
    }
  }, [isPlay]);

  // useEffect(() => {
  //   if (!wavesurferRef.current) return;

  //   wavesurferRef.current.setAutoCenter(autoCenter);
  // }, [autoCenter]);

  useEffect(() => {
    if (!wavesurferRef.current) return;

    setActiveSegment(activeSegmentProp);

    if (activeSegmentProp) {
      let zoomFactor = 500;
      const width = wavesurferRef.current.drawer.wrapper.clientWidth;
      const segmentDuration = activeSegmentProp.End - activeSegmentProp.Start;

      for (let i = 0; i < 10; i++) {
        if (segmentDuration * zoomFactor < width) break;
        zoomFactor /= 2;
      }
      if (zoomFactor < 5) zoomFactor = 5;

      setZoomClick(zoomFactor);
      setAutoCenter(false);
      setIsPlay(true);

      const total = wavesurferRef.current.getDuration();
      if (total > 0) {
        const centerTime = activeSegmentProp.Start + (activeSegmentProp.End - activeSegmentProp.Start) / 2;
        const t1 = centerTime / total;

        wavesurferRef.current.zoom(zoomFactor);
        wavesurferRef.current.seekAndCenter(t1);
        wavesurferRef.current.setCurrentTime(activeSegmentProp.Start);
        wavesurferRef.current.play();
      }
    }

    regions.forEach(region => {
      if (region.color !== "#6ab4ff29") {
        region.color = "#6ab4ff29";
      }

      if (activeSegmentProp?.id === region.id) {
        region.color = "#2692ff40";
      }
    });

    const subscription = currTimeSubscriber.subscribe(currentTime => {
      if (wavesurferRef.current && activeSegmentProp && currentTime >= activeSegmentProp.End) {
        wavesurferRef.current.setCurrentTime(activeSegmentProp.Start);
      }
    });

    return () => {
      subscription.unsubscribe();
    };
  }, [activeSegmentProp, regions]);

  return (
    <div style={{ width: "100%" }}>
      <WaveSurfer onMount={handleWSMount} plugins={plugins}>
        <WaveForm id="waveform" waveColor="#b1d0f1" progressColor="#b1d0f1" cursorColor="red" autoCenter={autoCenter}>
          {regions.map(regionProps => (
            <Region key={regionProps.id} {...regionProps} />
          ))}
        </WaveForm>
        <div style={{ height: "20px" }} id="timeline" />
      </WaveSurfer>

      <div style={{ height: "40px" }}>
        <IconButton
          color="primary"
          onClick={(e: any) => {
            (document.activeElement as HTMLElement).blur();
            setIsPlay((prev) => !prev);
          }}
        >
          {isPlay ? <PauseCircleFilledIcon fontSize="medium" /> : <PlayCircleFilledIcon fontSize="medium" />}
        </IconButton>
        <IconButton
          color="primary"
          title="Увеличить масштаб"
          onClick={(e: any) => {
            (document.activeElement as HTMLElement).blur();
            setZoomClick(zoom * 2);
          }}
        >
          <ZoomInIcon fontSize="medium" />
        </IconButton>
        <IconButton
          color="primary"
          title="Уменьшить масштаб"
          onClick={(e: any) => {
            (document.activeElement as HTMLElement).blur();
            setZoomClick(zoom / 2);
          }}
        >
          <ZoomOutIcon fontSize="medium" />
        </IconButton>
        <IconButton
          color="primary"
          title="Скачать аудио"
          onClick={(e: any) => {
            (document.activeElement as HTMLElement).blur();
            downloadAudio();
          }}
        >
          <GetAppIcon fontSize="medium" />
        </IconButton>

        <FormControlLabel
          style={{ marginLeft: "10px" }}
          onClick={(e: any) => {
            (document.activeElement as HTMLElement).blur();
          }}
          control={
            <Checkbox
              checked={autoCenter}
              color="primary"
              onChange={(event: any) => {
                setAutoCenter(event.target.checked);
              }}
            />
          }
          label="Позиция по центру"
        />
      </div>
    </div>
  );
};

export default WavePlayer;
