// src/Tiptap.jsx
import { EditorProvider, FloatingMenu, BubbleMenu, useCurrentEditor } from '@tiptap/react'
import StarterKit from '@tiptap/starter-kit'
import TextStyle from '@tiptap/extension-text-style'
import TextAlign from '@tiptap/extension-text-align';
import Highlight from '@tiptap/extension-highlight';
import Color from '@tiptap/extension-color';
import Underline from '@tiptap/extension-underline';
// import './editor.scss'
import React, { useState } from 'react'
import ListItem from '@tiptap/extension-list-item'

//material
import { Button, Paper, Slider } from '@material-ui/core';
import FormatBoldIcon from "@material-ui/icons/FormatBold";
import IconButton from '@material-ui/core/IconButton';
import { FormatItalic } from '@material-ui/icons';
import { FormatStrikethrough } from '@material-ui/icons';
import FormatListBulletedIcon from '@material-ui/icons/FormatListBulleted';
import FormatListNumberedIcon from '@material-ui/icons/FormatListNumbered';
import UndoIcon from '@material-ui/icons/Undo';
import RedoIcon from '@material-ui/icons/Redo';
import BorderColorIcon from '@material-ui/icons/BorderColor';
import TextFormatIcon from '@material-ui/icons/TextFormat';
import FormatSizeIcon from '@material-ui/icons/FormatSize';
import FormatUnderlinedIcon from '@material-ui/icons/FormatUnderlined';
import FormatClearIcon from '@material-ui/icons/FormatClear';
import FormatAlignLeftIcon from '@material-ui/icons/FormatAlignLeft';
import FormatAlignCenterIcon from '@material-ui/icons/FormatAlignCenter';
import FormatAlignRightIcon from '@material-ui/icons/FormatAlignRight';
import EditorStyled from "./style";
import ToolbarButtons from './ToolbarButtons';

const MenuBar = () => {
  const { editor } = useCurrentEditor()

  if (!editor) {
    return null
  }

  return (
    <>
     {/* <div style={{display: 'flex', flexDirection: 'row-reverse', alignItems: 'center', justifyContent: 'space-between', margin: '1rem 0'}}>
      <div className='aditionalButtons' style={{display: 'flex', flexDirection: 'row', alignItems: 'center', justifyContent: 'flex-end', gap: '1rem'}}>
      <Button
      variant='outlined'
      color='primary'
      size='small'
      onClick={() => console.log('clicked')}
      >
        Скачать
      </Button>
      <Button
      variant='outlined'
      color='primary'
      size='small'
      onClick={() => console.log('clicked')}
      >
        Копировать
      </Button>
      <Button
      variant='contained'
      color='primary'
      size='small'
      onClick={() => console.log('clicked')}
      >
        Сохранить
      </Button>
      </div>
      <div style={{display: 'flex', flexDirection: 'row', justifyContent: 'center', alignItems: 'center', margin: '0 1rem'}}>
      <p style={{marginRight: '2rem'}}>Confidence:</p>
      <Slider
        min={0}
        max={100}
        // value={confidence}
        // onChange={handleSliderChange}
        defaultValue={0}
        aria-labelledby="discrete-slider"
        valueLabelDisplay="auto"
        // color='primary'
        marks
        step={1}
        style={{ width: '125px'}}
      />
      </div>
      </div>  */}
    <div style={{display: 'flex', flexDirection: 'row'}}>
      <IconButton 
        color="primary" 
        title="Жирный"
        onClick={() => editor.chain().focus().toggleBold().run()}
        disabled={
          !editor.can()
            .chain()
            .focus()
            .toggleBold()
            .run()
        }
        className={editor.isActive('bold') ? 'is-active' : ''}
        >
        <FormatBoldIcon />
      </IconButton>
      <IconButton
        onClick={() => editor.chain().focus().toggleItalic().run()}
        disabled={
          !editor.can()
            .chain()
            .focus()
            .toggleItalic()
            .run()
        }
        color='primary'
        title='Курсив'
        className={editor.isActive('italic') ? 'is-active' : ''}
      >
        <FormatItalic />
        </IconButton>
        <IconButton
        onClick={() => editor.chain().focus().toggleStrike().run()}
        disabled={
          !editor.can()
            .chain()
            .focus()
            .toggleStrike()
            .run()
        }
        color="primary"
        title="Зачеркнутый"
        className={editor.isActive('strike') ? 'is-active' : ''}
      >
        <FormatStrikethrough />
      </IconButton>
      <IconButton
        onClick={() => editor.chain().focus().toggleHighlight().run()}
        disabled={
          !editor.can()
            .chain()
            .focus()
            // .toggleHighlight()
            .run()
        }
        color="primary"
        title="Выделить цветом"
        className={editor.isActive('highlight') ? 'is-active' : ''}
      >
        <BorderColorIcon />
      </IconButton>
      <IconButton 
        onClick={() => editor.chain().focus().unsetAllMarks().run()}
        color="primary"
        title="Убрать выделение"
        disabled={
          !editor.can()
            .chain()
            .focus()
            .unsetAllMarks()
            .run()
        }
        >
        <FormatClearIcon />
      </IconButton>
      <IconButton
        onClick={() => editor.chain().focus().setTextAlign('left').run()}
        className={editor.isActive({ textAlign: 'left' }) ? 'is-active' : ''}
        // color={editor.isFocused ? "primary" : 'default'}
        color="primary"
        title="Выравнивание по левому краю"
      >
        <FormatAlignLeftIcon />
      </IconButton>
      <IconButton
        onClick={() => editor.chain().focus().setTextAlign('center').run()}
        className={editor.isActive({ textAlign: 'center' }) ? 'is-active' : ''}
        // color={editor.isFocused ? "primary" : 'default'}
        color="primary"
        title="Выравнивание по центру"
      >
        <FormatAlignCenterIcon />
      </IconButton>
      <IconButton
        onClick={() => editor.chain().focus().setTextAlign('right').run()}
        // color={editor.isFocused ? "primary" : 'default'}
        color="primary"
        title="Выравнивание по правому краю"
      >
        <FormatAlignRightIcon />
      </IconButton>
      {/* <button onClick={() => editor.chain().focus().clearNodes().run()}>
        Отчистить текст
      </button> */}
      {/* <button
        onClick={() => editor.chain().focus().setParagraph().run()}
        className={editor.isActive('paragraph') ? 'is-active' : ''}
      >
        paragraph
      </button> */}
      {/* <button
        onClick={() => editor.chain().focus().toggleHeading({ level: 1 }).run()}
        className={editor.isActive('heading', { level: 1 }) ? 'is-active' : ''}
      >
        h1
      </button>
      <button
        onClick={() => editor.chain().focus().toggleHeading({ level: 2 }).run()}
        className={editor.isActive('heading', { level: 2 }) ? 'is-active' : ''}
      >
        h2
      </button>
      <button
        onClick={() => editor.chain().focus().toggleHeading({ level: 3 }).run()}
        className={editor.isActive('heading', { level: 3 }) ? 'is-active' : ''}
      >
        h3
      </button>
      <button
        onClick={() => editor.chain().focus().toggleHeading({ level: 4 }).run()}
        className={editor.isActive('heading', { level: 4 }) ? 'is-active' : ''}
      >
        h4
      </button>
      <button
        onClick={() => editor.chain().focus().toggleHeading({ level: 5 }).run()}
        className={editor.isActive('heading', { level: 5 }) ? 'is-active' : ''}
      >
        h5
      </button>
      <button
        onClick={() => editor.chain().focus().toggleHeading({ level: 6 }).run()}
        className={editor.isActive('heading', { level: 6 }) ? 'is-active' : ''}
      >
        h6
      </button> */}
      <IconButton
        onClick={() => editor.chain().focus().toggleBulletList().run()}
        className={editor.isActive('bulletList') ? 'is-active' : ''}
        color="primary"
        title="Ненумерованный список"
      >
        <FormatListBulletedIcon/>
        </IconButton>
        <IconButton
        onClick={() => editor.chain().focus().toggleOrderedList().run()}
        className={editor.isActive('orderedList') ? 'is-active' : ''}
        color="primary"
        title="Нумерованный список"
      >
        <FormatListNumberedIcon />
        </IconButton>
      {/* <button onClick={() => editor.chain().focus().setHorizontalRule().run()}>
        horizontal rule
      </button> */}
      {/* <button onClick={() => editor.chain().focus().setHardBreak().run()}>
        hard break
      </button> */}
      <IconButton
        onClick={() => editor.chain().focus().undo().run()}
        disabled={
          !editor.can()
            .chain()
            .focus()
            .undo()
            .run()
        }
        color="primary"
        title="Действие назад"
      >
        <UndoIcon />
        </IconButton>
      <IconButton
        onClick={() => editor.chain().focus().redo().run()}
        disabled={
          !editor.can()
            .chain()
            .focus()
            .redo()
            .run()
        }
        color="primary"
        title="Действие вперёд"
      >
        <RedoIcon />
        </IconButton>
      </div>
    </>
  )
}



const extensions = [
  Color.configure({ types: [TextStyle.name, ListItem.name] }),
  // TextStyle.configure({ types: [ListItem.name] }),
  StarterKit.configure({
    bulletList: {
      keepMarks: true,
      keepAttributes: false, 
    },
    orderedList: {
      keepMarks: true,
      keepAttributes: false, 
    },
  }),
  TextStyle.extend({
    addAttributes() {
    return {
    style: {
    // default: this.options.HTMLAttributes.style
    }
    }
    },
    }),
  Highlight.configure({
    HTMLAttributes: {
      class: 'my-custom-class',
    },
  }),
  TextAlign.configure({
    alignments: ['left', 'center', 'right'],
  })

