import React from 'react';
import { Tooltip } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

interface KeywordMarkerProps {
    keyword: {
      Start: number;
      End: number;
      Name: string;
      ChannelNumber: number;
      Confidence: number;
    };
    color: string;
    duration: number;
    canvasWidth: number;
    canvasHeight: number;
    zoomLevel: number;
    isExpanded: boolean;
    currentTime: number;
  }

const useStyles = makeStyles((theme) => ({
  marker: {
    position: 'absolute',
    width: '2px',
    backgroundColor: 'rgba(255, 0, 0, 0.7)',
    cursor: 'pointer',
  },
}));

const KeywordMarker: React.FC<KeywordMarkerProps> = ({ 
    keyword, 
    color, 
    duration, 
    canvasWidth, 
    canvasHeight, 
    zoomLevel,
    isExpanded,
    currentTime
  }) => {
    const classes = useStyles();

    const visibleDuration = duration / zoomLevel;
    const startTime = Math.max(0, currentTime - visibleDuration / 2);
    const endTime = Math.min(duration, startTime + visibleDuration);

    const markerPosition = ((keyword.Start - startTime) / visibleDuration) * canvasWidth;
    const markerHeight = isExpanded && keyword.ChannelNumber === 2 ? canvasHeight / 2 : canvasHeight;
    const markerTop = isExpanded && keyword.ChannelNumber === 2 ? canvasHeight / 2 : 0;

    // Проверяем, находится ли маркер в видимой области
    if (keyword.Start < startTime || keyword.Start > endTime) {
      return null;
    }

    return (
      <Tooltip title={`${keyword.Name} (${keyword.Confidence.toFixed(2)})`} placement="top">
        <div
          className={classes.marker}
          style={{
            left: `${markerPosition}px`,
            height: `${markerHeight}px`,
            top: `${markerTop}px`,
            backgroundColor: color,
          }}
        />
      </Tooltip>
    );
  };

export default KeywordMarker;
