import React, { useState } from 'react';
import { Box, Typography, Slider } from '@material-ui/core';

const Timestamps: React.FC = () => {
  const [blockSize, setBlockSize] = useState<number>(5);

  const timeBlocks = 595 / blockSize;

  const handleSliderChange = (event: any, newValue: number | number[]) => {
    if (typeof newValue === 'number') {
      setBlockSize(newValue);
    }
  };

  const formatTime = (seconds: number): string => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;

    const format = (val: number) => String(val).padStart(2, '0');
    return `00:${format(minutes)}:${format(remainingSeconds)}`;
  };

  const handleBlockClick = (time: number) => {
    alert(`Время начала: ${formatTime(time)}`);
  }

  return (
    <Box style={{margin: '0.5rem'}}>
      <Slider
        min={10}
        max={30}
        value={blockSize}
        onChange={handleSliderChange}
        aria-labelledby="step-slider"
        valueLabelDisplay="auto"
        marks={true}
        step={10}
        style={{width: '200px',marginTop: '3rem'}}
      />
      <Box display="flex" flexDirection="column" style={{width: '200px', gap: '1rem', overflow: 'scroll', height: '800px', position: 'relative', }}>
      {Array.from(Array(timeBlocks)).map((_, index) => (
        <Box key={index} onClick={() => handleBlockClick(index * blockSize)} style={{ cursor: 'pointer', padding: '10px', background: 'lightblue' }}>
          <Typography>{formatTime(index * blockSize)}</Typography>
          <Typography>{formatTime((index + 1) * blockSize)}</Typography>
        </Box>
      ))}
      </Box>
    </Box>
  );
};

export default Timestamps;
