// AudioPlayer.tsx
import React, { useRef, useEffect, useState } from 'react';
import { useAudio } from './AudioContext';
import styled from 'styled-components';

const PlayerContainer = styled.div`
  background-color: #2c3e50;
  padding: 20px;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const PlayButton = styled.button`
  background-color: #3498db;
  color: white;
  border: none;
  padding: 10px 20px;
  font-size: 16px;
  border-radius: 4px;
  cursor: pointer;
  transition: background-color 0.3s;

  &:hover {
    background-color: #2980b9;
  }

  &:focus {
    outline: none;
    box-shadow: 0 0 0 2px rgba(52, 152, 219, 0.5);
  }
`;

const Canvas = styled.canvas`
  margin-top: 20px;
  background-color: #34495e;
  border-radius: 4px;
`;


const AudioPlayer: React.FC = () => {
  const { currentAudioUrl } = useAudio();
  const audioContextRef = useRef<AudioContext | null>(null);
  const sourceNodeRef = useRef<AudioBufferSourceNode | null>(null);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [isPlaying, setIsPlaying] = useState(false);

  useEffect(() => {
    audioContextRef.current = new (window.AudioContext || (window as any).webkitAudioContext)();
    return () => {
      audioContextRef.current?.close();
    };
  }, []);

  const playAudio = async () => {
    if (!audioContextRef.current || !currentAudioUrl) return;

    if (sourceNodeRef.current) {
      sourceNodeRef.current.stop();
    }

    try {
        const response = await fetch(currentAudioUrl);
        const arrayBuffer = await response.arrayBuffer();
        const audioBuffer = await audioContextRef.current.decodeAudioData(arrayBuffer);

        sourceNodeRef.current = audioContextRef.current.createBufferSource();
        sourceNodeRef.current.buffer = audioBuffer;
        sourceNodeRef.current.connect(audioContextRef.current.destination);

        const analyser = audioContextRef.current.createAnalyser();
        sourceNodeRef.current.connect(analyser);

        sourceNodeRef.current.start();
        setIsPlaying(true);

        drawOscilloscope(analyser);
        } catch (error) {
            console.error('Error loading audio:', error);
            setIsPlaying(false);
        }
        };


  const drawOscilloscope = (analyser: AnalyserNode) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const canvasCtx = canvas.getContext('2d');
    if (!canvasCtx) return;

    const draw = () => {
      const WIDTH = canvas.width;
      const HEIGHT = canvas.height;

      analyser.fftSize = 2048;
      const bufferLength = analyser.fftSize;
      const dataArray = new Uint8Array(bufferLength);

      canvasCtx.clearRect(0, 0, WIDTH, HEIGHT);

      const drawVisual = requestAnimationFrame(draw);

      analyser.getByteTimeDomainData(dataArray);

      canvasCtx.lineWidth = 2;

      canvasCtx.strokeStyle = 'rgb(0, 255, 0)';
      canvasCtx.beginPath();

      const sliceWidth = WIDTH * 1.0 / bufferLength;
      let x = 0;

      for (let i = 0; i < bufferLength; i++) {
        const v = dataArray[i] / 128.0;
        const y = v * HEIGHT / 2;

        if (i === 0) {
          canvasCtx.moveTo(x, y);
        } else {
          canvasCtx.lineTo(x, y);
        }

        x += sliceWidth;
      }

      canvasCtx.lineTo(canvas.width, canvas.height / 2);
      canvasCtx.stroke();
    };

    draw();
  };

  const stopAudio = () => {
    if (sourceNodeRef.current) {
      sourceNodeRef.current.stop();
      setIsPlaying(false);
    }
  };

  // Добавим отображение текущего аудиофайла
    const AudioInfo = styled.div`
    color: white;
    margin-bottom: 10px;
    font-size: 14px;
    `;

  return (
    <PlayerContainer>
    <AudioInfo>
      {currentAudioUrl ? `Now playing: ${currentAudioUrl.split('/').pop()}` : 'No audio selected'}
    </AudioInfo>
    <PlayButton onClick={isPlaying ? stopAudio : playAudio}>
      {isPlaying ? 'Stop' : 'Play'}
    </PlayButton>
    <Canvas ref={canvasRef} width="800" height="200" />
  </PlayerContainer>
  );
};

export default AudioPlayer;
