import React, { FC, useState, useCallback } from "react";
import { IconButton, Paper } from "@mui/material";
import ExpandMoreIcon from "@mui/icons-material/ExpandMore";
import ExpandLessIcon from "@mui/icons-material/ExpandLess";
import ExpandedPlayer from "./ExpandedPlayer";
import CompactPlayer from "./CompactPlayer";

// const TimelinePlugin = require("wavesurfer.js/dist/plugin/wavesurfer.timeline.min");

interface Props {
    url: string;
}

const SuperPlayer: FC<Props> = ({ url }) => {
    const [isPlay, setIsPlay] = useState<boolean>(false);
    const [isExpanded, setIsExpanded] = useState<boolean>(false);
    const [currentTime, setCurrentTime] = useState<number>(0);
    const [zoom, setZoom] = useState<number>(10);

    const togglePlay = useCallback(() => {
      setIsPlay((prev) => !prev);
    }, []);

    return (
      <Paper
        elevation={3}
        style={{
          position: "fixed",
          bottom: 0,
          left: 0,
          width: "100%",
          padding: "10px",
          boxSizing: "border-box",
          transition: "height 0.3s ease",
          height: isExpanded ? "400px" : "80px",
          overflow: isExpanded ? "auto" : "hidden",
        }}
      >
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          {isExpanded ? (
            <ExpandedPlayer
              url={url}
              isPlay={isPlay}
              togglePlay={togglePlay}
              currentTime={currentTime}
              setCurrentTime={setCurrentTime}
              zoom={zoom}
              setZoom={setZoom}
            />
          ) : (
            <CompactPlayer
              url={url}
              isPlay={isPlay}
              togglePlay={togglePlay}
              currentTime={currentTime}
              setCurrentTime={setCurrentTime}
            />
          )}
          <IconButton color="primary" size="medium" onClick={() => setIsExpanded((prev) => !prev)}>
            {isExpanded ? <ExpandMoreIcon fontSize="large" /> : <ExpandLessIcon fontSize="large" />}
          </IconButton>
        </div>
      </Paper>
    );
  };

  export default SuperPlayer;
