import React, { useRef, useEffect, useState } from 'react';
import styled from 'styled-components';
import { KeywordColor, SegmentModel } from './AudioContext';
import { Tooltip } from '@material-ui/core';

const WaveformContainer = styled.div`
  width: 100%;
  height: 100px;
  position: relative;
  background-color: #f1f3f4;
  border-radius: 4px;
  overflow: hidden;
  cursor: pointer;
  padding: 30px 0px;
`;

const WaveformCanvas = styled.canvas`
  width: 100%;
  height: 100%;
  background-color: #f4h2a1;
`;

const ProgressOverlay = styled.div<{ progress: number }>`
  position: absolute;
  top: 0;
  left: 0;
  height: 100%;
  width: ${props => props.progress}%;
  background-color: rgba(255, 85, 0, 0.2);
  pointer-events: none;
`;

interface AudioWaveformProps {
  audioUrl: string;
  currentTime: number;
  duration: number;
  isPlaying: boolean;
  onSeek: (time: number) => void;
  onPlayPause: () => void;
  segments: SegmentModel[];
  keywords: KeywordColor[];
}

const AudioWaveform: React.FC<AudioWaveformProps> = ({
  audioUrl,
  currentTime,
  duration,
  isPlaying,
  onSeek,
  onPlayPause,
  segments,
  keywords
}) => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const containerRef = useRef<HTMLDivElement>(null);
  const [waveformData, setWaveformData] = useState<number[]>([]);
  const [hoveredKeyword, setHoveredKeyword] = useState<KeywordColor | null>(null);
  const [tooltipPosition, setTooltipPosition] = useState({ top: 0, left: 0 });

  useEffect(() => {
    if (audioUrl) {
      fetchAudioAndComputeWaveform(audioUrl);
    }
  }, [audioUrl]);

  useEffect(() => {
    drawWaveform();
  }, [waveformData, currentTime]);

  const fetchAudioAndComputeWaveform = async (url: string) => {
    try {
      const response = await fetch(url);
      const arrayBuffer = await response.arrayBuffer();
      const audioContext = new (window.AudioContext || (window as any).webkitAudioContext)();
      const audioBuffer = await audioContext.decodeAudioData(arrayBuffer);
      const waveform = computeWaveform(audioBuffer);
      setWaveformData(waveform);
    } catch (error) {
      console.error('Error fetching audio:', error);
    }
  };

  const computeWaveform = (audioBuffer: AudioBuffer) => {
    const channelData = audioBuffer.getChannelData(0);
    const samples = 500;
    const blockSize = Math.floor(channelData.length / samples);
    const waveform = [];

    for (let i = 0; i < samples; i++) {
      const start = i * blockSize;
      const end = start + blockSize;
      let max = 0;
      for (let j = start; j < end; j++) {
        const amplitude = Math.abs(channelData[j]);
        if (amplitude > max) {
          max = amplitude;
        }
      }
      waveform.push(max);
    }

    return waveform;
  };

  const drawWaveform = () => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    const width = canvas.width;
    const height = canvas.height;
    const barWidth = width / waveformData.length;


    ctx.clearRect(0, 0, width, height);

    waveformData.forEach((value, index) => {
      const x = index * barWidth;
      const barHeight = value * height;

      ctx.fillStyle = currentTime / duration > index / waveformData.length ? '#ff5500' : '#999';
      ctx.fillRect(x, (height - barHeight) / 2, barWidth - 1, barHeight);
    });

    segments.forEach((segment) => {
      const startX = (segment.Start / duration) * width;
      const endX = (segment.End / duration) * width;
      ctx.fillStyle = segment.Words && segment.Words[0].NormalForm === "[music]"
        ? "rgba(181, 201, 255, 0.5)"
        : "rgba(0, 123, 255, 0.5)";
      ctx.fillRect(startX, 0, endX - startX, height);
    });

    // Отрисовка ключевых слов
    keywords.forEach((kw) => {
      const startX = (kw.keyword.Start / duration) * width;
      ctx.fillStyle = kw.color;
      ctx.fillRect(startX, 0, 0.5, height);
    });

  };

  const handleClick = (e: React.MouseEvent<HTMLDivElement>) => {
    if (!containerRef.current) return;
    const rect = containerRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const seekTime = (x / rect.width) * duration;
    onSeek(seekTime);
  };

  const handleMouseMove = (e: React.MouseEvent<HTMLDivElement>) => {
    const rect = containerRef.current!.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const time = (x / rect.width) * duration;

    const hoveredKw = keywords.find(kw => 
      time >= kw.keyword.Start && time <= kw.keyword.End
    );

    setHoveredKeyword(hoveredKw || null);
    setTooltipPosition({ top: e.clientY, left: e.clientX });
  };

  const handleMouseLeave = () => {
    setHoveredKeyword(null);
  };


  return (
    <WaveformContainer 
      ref={containerRef} 
      onClick={handleClick} 
      onMouseMove={handleMouseMove}
      onMouseLeave={handleMouseLeave}
    >
      <WaveformCanvas ref={canvasRef} width={1000} height={200} />
      <ProgressOverlay progress={(currentTime / duration) * 100} />
      {hoveredKeyword && (
        <Tooltip
          title={`${hoveredKeyword.keyword.Name} (${formatTime(hoveredKeyword.keyword.Start)} - ${formatTime(hoveredKeyword.keyword.End)})`}
          open={true}
          style={{
            position: 'absolute',
            top: tooltipPosition.top,
            left: tooltipPosition.left,
          }}
        >
          <div />
        </Tooltip>
      )}
    </WaveformContainer>
  );
};

const formatTime = (time: number) => {
  const minutes = Math.floor(time / 60);
  const seconds = Math.floor(time % 60);
  return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
};


export default AudioWaveform;
