import React from 'react';
import { Tooltip } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';

interface KeywordMarkerProps {
  keyword: {
    Start: number;
    End: number;
    Name: string;
    ChannelNumber: number;
    Confidence: number;
  };
  duration: number;
  canvasWidth: number;
  canvasHeight: number;
  color: string;
}

const useStyles = makeStyles((theme) => ({
  marker: {
    position: 'absolute',
    width: '2px',
    backgroundColor: 'rgba(255, 0, 0, 0.7)',
    cursor: 'pointer',
  },
}));

const KeywordMarker: React.FC<KeywordMarkerProps> = ({ keyword, color, duration, canvasWidth, canvasHeight }) => {
    const classes = useStyles();

    const markerPosition = (keyword.Start / duration) * canvasWidth;
    const markerHeight = canvasHeight;

    return (
      <Tooltip title={`${keyword.Name} (${keyword.Confidence.toFixed(2)})`} placement="top">
        <div
          className={classes.marker}
          style={{
            left: `${markerPosition}px`,
            height: `${markerHeight}px`,
            top: 0,
            backgroundColor: color,
          }}
        />
      </Tooltip>
    );
  };

export default KeywordMarker;
