import { FC, useMemo, useRef, useState, useEffect, useCallback } from "react";
import IconButton from "@material-ui/core/IconButton";
import PlayCircleFilledIcon from "@material-ui/icons/PlayCircleFilled";
import PauseCircleFilledIcon from "@material-ui/icons/PauseCircleFilled";
import ZoomInIcon from "@material-ui/icons/ZoomIn";
import ZoomOutIcon from "@material-ui/icons/ZoomOut";
import { WaveSurfer, WaveForm } from "wavesurfer-react";
import { currTimeSubscriber } from "../../subscribers/PlayerSubscriber";
import { SegmentModel as Segment } from "../AudioContext";
import LeftChannelIcon from "@material-ui/icons/ArrowLeft"; // для левого канала
import RightChannelIcon from "@material-ui/icons/ArrowRight";

interface KeywordColor {
  keyword: {
    Start: number;
    Name: string;
    ChannelNumber: number;
    Confidence: number;
  };
  color: string;
}

interface Props {
  file: File | null;
  activeSegmentProp: Segment | null;
  url: string;
  keywords: KeywordColor[];
  channelCount: number;
}

const TimelinePlugin = require("wavesurfer.js/dist/plugin/wavesurfer.timeline.min");

const WordMarker: FC<{keyword: KeywordColor['keyword'], color: string, wavesurfer: any, zoom: number, isReady: boolean, channelCount: number}> = ({ keyword, color, wavesurfer, zoom, isReady, channelCount }) => {
    const markerRef = useRef<HTMLDivElement>(null);

    useEffect(() => {
        if (!isReady) return;
      const updatePosition = () => {
        if (markerRef.current && wavesurfer) {
          const duration = wavesurfer.getDuration();
          const containerWidth = wavesurfer.drawer.width;
          const scrollLeft = wavesurfer.drawer.getScrollX();

          const markerPosition = (keyword.Start / duration) * containerWidth;
          const adjustedPosition = markerPosition - scrollLeft;

          markerRef.current.style.left = `${adjustedPosition}px`;
          markerRef.current.style.display = adjustedPosition >= 0 && adjustedPosition <= containerWidth ? 'block' : 'none';
        }
      };

      updatePosition();
      wavesurfer.on('scroll', updatePosition);
      wavesurfer.on('zoom', updatePosition);

      return () => {
        wavesurfer.un('scroll', updatePosition);
        wavesurfer.un('zoom', updatePosition);
      };
    }, [keyword, wavesurfer, zoom, isReady]);

  const handleClick = () => {
    wavesurfer.setCurrentTime(keyword.Start);
  };

  const markerHeight = channelCount === 1 ? '34%' : '100%';

  return (
    <div
      ref={markerRef}
      onClick={handleClick}
      style={{
        position: 'absolute',
        top: 0,
        height: markerHeight,
        width: '2px',
        backgroundColor: color,
        cursor: 'pointer',
      }}
    >
      <div
        style={{
          position: 'absolute',
          top: '-20px',
          left: '50%',
          transform: 'translateX(-50%)',
          backgroundColor: color,
          padding: '2px 5px',
          borderRadius: '3px',
          color: 'white',
          fontSize: '12px',
        }}
      >
        {keyword.Name}
      </div>
    </div>
  );
};

const WaveKeywordsNoRegions: FC<Props> = ({ file, activeSegmentProp, url, keywords, channelCount }) => {
    const [isPlay, setIsPlay] = useState<boolean>(false);
    const [autoCenter, setAutoCenter] = useState<boolean>(true);
    const [zoom, setZoom] = useState<number>(10);
    const [activeSegment, setActiveSegment] = useState<Segment | null>(activeSegmentProp);
    const [isWavesurferReady, setIsWavesurferReady] = useState<boolean>(false);

    const [activeChannel, setActiveChannel] = useState<number | null>(null); // null означает оба канала активны

    const wavesurferRef = useRef<any>(null);
    const audioContextRef = useRef<AudioContext | null>(null);
    const splitterRef = useRef<ChannelSplitterNode | null>(null);
    const mergerRef = useRef<ChannelMergerNode | null>(null);
    const leftGainRef = useRef<GainNode | null>(null);
    const rightGainRef = useRef<GainNode | null>(null);

    const initializeAudioContext = (waveSurfer: any) => {
        if (!audioContextRef.current) {
          const context = new AudioContext();
          audioContextRef.current = context;

          const source = context.createMediaElementSource(waveSurfer.backend.media);
          const splitter = context.createChannelSplitter(2);
          const merger = context.createChannelMerger(2);

          const leftGain = context.createGain();
          const rightGain = context.createGain();

          leftGain.gain.value = 1;
          rightGain.gain.value = 1;

          source.connect(splitter);
          splitter.connect(leftGain, 0);
          splitter.connect(rightGain, 1);

          leftGain.connect(merger, 0, 0);
          rightGain.connect(merger, 0, 1);

          merger.connect(context.destination);

          splitterRef.current = splitter;
          mergerRef.current = merger;
          leftGainRef.current = leftGain;
          rightGainRef.current = rightGain;
        }
      };

    const plugins = useMemo(() => [
      {
        plugin: TimelinePlugin,
        options: { container: "#timeline" },
      },
    ], []);

    const handleWSMount = useCallback((waveSurfer) => {
        wavesurferRef.current = waveSurfer;
        wavesurferRef.current.load(url);

        wavesurferRef.current.on("ready", () => {
          setIsWavesurferReady(true);
          initializeAudioContext(waveSurfer);  // Инициализация аудиоконтекста
        });

        let lastTime = 0;
        wavesurferRef.current.on("audioprocess", (currentTime: number) => {
          const diff = Math.abs(currentTime - lastTime);
          if (diff > 0.1) {
            lastTime = currentTime;
            currTimeSubscriber.next(currentTime);
          }
        });
      }, [url]);

    const setZoomClick = (zoom: number) => {
      if (zoom < 5) zoom = 5;
      if (zoom > 1000) zoom = 1000;
      setZoom(zoom);
    };

    const playChannel = (leftGainValue: number, rightGainValue: number) => {
        if (leftGainRef.current && rightGainRef.current) {
          leftGainRef.current.gain.value = leftGainValue;
          rightGainRef.current.gain.value = rightGainValue;
        }
      };

    const playLeftChannel = () => {
        setActiveChannel(0);
        playChannel(1, 0);
    };

    const playRightChannel = () => {
        setActiveChannel(1);
        playChannel(0, 1);
    };

    const playBothChannels = () => {
        setActiveChannel(null);
        playChannel(1, 1);
    };


    useEffect(() => {
      if (!wavesurferRef.current) return;

      setZoom(10);

      if (url === "") return;

      wavesurferRef.current.load(url);
    }, [file, url]);

    useEffect(() => {
        if (wavesurferRef.current) {
        wavesurferRef.current.zoom(zoom);
        wavesurferRef.current.fireEvent('zoom', zoom);
        }
    }, [zoom]);

    useEffect(() => {
      if (!wavesurferRef.current) return;

      if (isPlay) {
        wavesurferRef.current.play();
      } else {
        wavesurferRef.current.pause();
      }
    }, [isPlay]);

    useEffect(() => {
      if (!wavesurferRef.current) return;

      setActiveSegment(activeSegmentProp);

      if (activeSegmentProp) {
        let zoomFactor = 500;
        const width = wavesurferRef.current.drawer.wrapper.clientWidth;
        const segmentDuration = activeSegmentProp.End - activeSegmentProp.Start;

        for (let i = 0; i < 10; i++) {
          if (segmentDuration * zoomFactor < width) break;
          zoomFactor /= 2;
        }
        if (zoomFactor < 5) zoomFactor = 5;

        setZoomClick(zoomFactor);
        setAutoCenter(false);
        setIsPlay(true);

        const total = wavesurferRef.current.getDuration();
        if (total > 0) {
          const centerTime = activeSegmentProp.Start + (segmentDuration) / 2;
          const t1 = centerTime / total;

          wavesurferRef.current.zoom(zoomFactor);
          wavesurferRef.current.seekAndCenter(t1);
          wavesurferRef.current.setCurrentTime(activeSegmentProp.Start);
          wavesurferRef.current.play();
        }
      }

      const subscription = currTimeSubscriber.subscribe(currentTime => {
        if (wavesurferRef.current && activeSegmentProp && currentTime >= activeSegmentProp.End) {
          wavesurferRef.current.setCurrentTime(activeSegmentProp.Start);
        }
      });

      return () => {
        subscription.unsubscribe();
      };
    }, [activeSegmentProp]);

  return (
    <div style={{ width: "100%", position: "relative" }}>
      <WaveSurfer onMount={handleWSMount} plugins={plugins} >
        <WaveForm
          id="waveform"
          waveColor="#b7e1dd"
          progressColor="#4Db6AC"
          cursorColor="#357F78"
          autoCenter={autoCenter}
          splitChannels={false}
        >
        </WaveForm>
        <div style={{ height: "20px" }} id="timeline" />
      </WaveSurfer>

      {isWavesurferReady && wavesurferRef.current && keywords.map((kw, index) => (
        <WordMarker
            key={index}
            keyword={kw.keyword}
            color={kw.color}
            wavesurfer={wavesurferRef.current}
            zoom={zoom}
            isReady={isWavesurferReady}
            channelCount={1} // надо сюда прокидывать будет количество каналов
        />
      ))}

      <div style={{ height: "80px" }}>
        <IconButton
          color="primary"
          size="medium"
          onClick={(e: any) => {
            (document.activeElement as HTMLElement).blur();
            setIsPlay((prev) => !prev);
          }}
        >
          {isPlay ? <PauseCircleFilledIcon fontSize="large" /> : <PlayCircleFilledIcon fontSize="large" />}
        </IconButton>
        <IconButton
          color="primary"
          size="medium"
          title="Увеличить масштаб"
          onClick={(e: any) => {
            (document.activeElement as HTMLElement).blur();
            setZoomClick(zoom * 2);
          }}
        >
          <ZoomInIcon fontSize="large" />
        </IconButton>
        <IconButton
          color="primary"
          size="medium"
          title="Уменьшить масштаб"
          onClick={(e: any) => {
            (document.activeElement as HTMLElement).blur();
            setZoomClick(zoom / 2);
          }}
        >
          <ZoomOutIcon fontSize="large" />
        </IconButton>
         {/* Кнопки для управления каналами */}
         <IconButton
          color={activeChannel === 0 ? "primary" : "default"}
          size="medium"
          onClick={playLeftChannel}
          disabled={channelCount < 2}
        >
          <LeftChannelIcon fontSize="large" />
        </IconButton>
        <IconButton
          color={activeChannel === 1 ? "primary" : "default"}
          size="medium"
          onClick={playRightChannel}
          disabled={channelCount < 2}
        >
          <RightChannelIcon fontSize="large" />
        </IconButton>
        <IconButton
          color={activeChannel === null ? "primary" : "default"}
          size="medium"
          onClick={playBothChannels}
        >
          {/* Тут можно использовать либо кастомную иконку, либо что-то вроде <AllInclusiveIcon /> */}
          <ZoomOutIcon fontSize="large" />
        </IconButton>
      </div>
    </div>
  );
};

export default WaveKeywordsNoRegions;
