import React, { useState } from 'react';
import { Box, Typography, Slider } from '@material-ui/core';

const Timestamps: React.FC = () => {
  const [blockSize, setBlockSize] = useState<number>(5);

  const timeBlocks = 600 / blockSize;

  const handleSliderChange = (event: any, newValue: number | number[]) => {
    if (typeof newValue === 'number') {
      setBlockSize(newValue);
    }
  };

  const formatTime = (start: number, end: number): string => {
    const format = (time: number) => String(time).padStart(2, '0');
    return `${format(Math.floor(start / 60))}:${format(start % 60)} - ${format(Math.floor(end / 60))}:${format(end % 60)}`;
  };

  const handleBlockClick = (start: number, end: number) => {
    alert(`Время начала: ${formatTime(start, end)}`); // Вывод алерта с временем начала и конца блока при клике
  };

  return (
    <Box>
      <Slider
        min={10}
        max={30}
        value={blockSize}
        onChange={handleSliderChange}
        aria-labelledby="step-slider"
        valueLabelDisplay="auto"
        step={10}
        style={{width: '300px'}}
      />
      <Box display="flex" flexDirection="column" style={{width: '200px', gap: '1rem', overflow: 'auto'}}>
        {Array.from(Array(timeBlocks)).map((_, index) => (
          <Box key={index} onClick={() => handleBlockClick(index * blockSize, (index + 1) * blockSize)} style={{ cursor: 'pointer', padding: '10px', background: 'lightblue' }}>
            <Typography>{formatTime(index * blockSize, (index + 1) * blockSize)}</Typography>
          </Box>
        ))}
      </Box>
    </Box>
  );
};

export default Timestamps;
